/* ==UserStyle==
@name           rateyourmusic.com
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document domain("rateyourmusic.com") {
    /* Insert code here... */
    html {
        --dim-gray: #141212;
        --dark-gray: #181616;
        --medium-gray: #6b6969;
        --light-gray: #9a9898;

        --offwhite: #c8c093;
        --dim-blue: #2d4f67;

        --black: #0d0c0c;
        --red: #c4746e;
        --green: #8a9a7b;
        --yellow: #c4b28a;
        --blue: #8ba4b0 !important;
        --magenta: #a292a3;
        --cyan: #8ea4a2;
        --white: #c5c9c5;

        --bright-black: #a6a69c;
        --bright-red: #e46876;
        --bright-green: #87a987;
        --bright-yellow: #e6c384;
        --bright-blue: #7fb4ca;
        --bright-magenta: #938aa9;
        --bright-cyan: #7aa89f;
        --bright-white: #ffffff;
    }

    body {
        --background: var(--dark-gray);
        --header-background: var(--dark-gray);

        --surface-primary: var(--dark-gray);
        --surface-secondary: var(--dim-gray);

        --btn-expand-background-default: var(--dark-gray);
        --btn-expand-background-hover: var(--dark-gray);

        --btn-primary-background-default: var(--medium-gray);
        --btn-primary-background-hover: var(--blue);

        --btn-secondary-background-default: var(--mono-d8);
        --btn-secondary-background-hover: var(--mono-db);
        --btn-secondary-text: (--mono-3);;

        --mono-e: var(--dark-gray);
        --mono-ef: var(--dark-gray);
        --mono-f: var(--dim-gray);
        --mono-f0: var(--dark-gray);
        --mono-f4: var(--dark-gray);
        --mono-f7: var(--dark-gray);
        --mono-f8: var(--dark-gray);
        --mono-fb: var(--dark-gray);
        --mono-fa: var(--dark-gray);
    }

    /****************************
    ********* General ***********
    ****************************/

    #page_header {
        box-shadow: none;
    }

    #ui_search_main_search {
        background-color: var(--transparent);
    }

    #content_cover {
        background-color: #00000030;
    }

    footer {
        border: 0;
    }

    /****************************
    ******** Front Page *********
    ****************************/

    .page_home_section_featured_content_content .page_home_section_header h2 {
        margin-left: 3.4rem;
    }

    .ui_featured_content_comment {
        margin-left: 2rem;
        border: 0;

        h4 {
            color: var(--offwhite) !important;
        }
    }

    .page_home_section_featured_content_content_item_banner {
        top: .5rem;
        right: .5rem;
        background-color: transparent;
        box-shadow: none;
    }

    .page_home_section_featured_content_item_inner {
        padding: 1rem;
        border: 1px solid var(--ui-divider-line);
        border-radius: 6px;
    }

    .page_feature_main {
        background-color: transparent !important;    
    }

    .page_section_dynamic_fade_expand_button {
        color: var(--light-gray) !important;
        border-radius: 0 !important;

        &:hover {
            color: var(--bright-blue) !important;
        }
    }

    /****************************
    ********* Search ************
    ****************************/

    table:has(.infobox)[style="margin-bottom:0.5em;"] {
        border-collapse: separate;
        border: 1px solid var(--ui-divider-line);
        border-radius: 6px;
    }

    .infobox {        
        .mbgen {
            tr {
                background-color: transparent;
            }

            td {
                border: 0;
            }
        }
    }


    /****************************
    ********** User *************
    ****************************/

    .bubble_content:has(.profilehii) {
        padding-top: 2rem;
        padding-inline: 2rem;

        .btn.blue_btn {
            background-color: var(--btn-secondary-background-default);
            color: var(--btn-secondary-text);

            &:hover {
                background-color: var(--btn-secondary-background-hover);
            }
        }

        table:has(.profilehii) {
            td:nth-child(2),
            td:nth-child(3) {
                width: 33% !important;
            }

            #profile_image {
                vertical-align: middle !important;
                background-size: contain !important;
            }

            td:has(.profilehii) {
                vertical-align: middle !important;
            }

            td:has(#usermap) {
                width: 100%;
                height: 100%;
                vertical-align: middle !important;

                div {
                    margin: auto;
                }

                div[style="width:256px;"] {
                    width: 80% !important;
                }

                rect.overlay {
                    width: 100%;
                    height: 100%;
                }
            }
        }
    }

    .profilehii {
        table {
            border: 1px solid var(--ui-divider-line) !important;
            border-radius: 6px !important;
            border-collapse: separate;
        }

        td {
            border: 0 !important;
        }

        tr {
            background-color: transparent !important;
        }
    }

    #usermap {
        background: transparent;
    }

    .bubble_content:has(.profilesearch) {
        display: grid;
        grid-template-columns: auto auto;

        .musictoolbar {
            grid-row: 1;
            grid-column: 1 / span 1;
            align-self: center;
        }

        .profilesearch {
            margin:0;
            padding-right: 0.5rem;
            grid-row: 1;
            grid-column: 2 / span 1;
            align-self: center;
            justify-self: right;
            background: transparent !important;

            input,
            select {
                border-color: var(--ui-divider-line);
            }

            input[type="submit"] {
                line-height: 1.4em;
            }
        }

        table {
            grid-column: 1 / span 2;
            padding-bottom: 1rem;

            table {
                border-collapse: separate;
                border: 1px solid var(--ui-divider-line);
                border-radius: 6px;
            }

            tr {
                background-color: transparent;
            }

            td {
                border: 0;
            }
        }
    }

    .bubble_content:has(.profilesearch) ~ .clear ~ .bubble_header ~ .bubble_content {
        table {
            padding: .5rem;
            border: 1px solid var(--ui-divider-line) !important;
            border-radius: 6px;
            border-collapse: separate;
        }

        tr,
        td {
            background-color: transparent;
            border: 0;
        }

        tr:hover {
            background-color: var(--dim-gray);
        }
    }

    .profile_listening_container {
        margin-inline: auto;
        max-width: 900px;
        border: 1px solid var(--ui-divider-line);
        background-color: var(--dark-gray);
    }

    .musictoolbar a {
        background-color: var(--btn-secondary-background-default) !important;
        color: var(--btn-secondary-text) !important;

        &:hover {
            background-color: var(--btn-secondary-background-hover) !important;
        }

        &[style="background: var(--gen-blue-light);"] {
            background-color: var(--dim-blue) !important;
        }
    }

    .bubble_content:has(#ftabfriends) {
        .musictoolbar {
            padding: 0 !important;
        }

        table {
            border-radius: 6px;
            border-collapse: separate;
        }

        th,
        tr,
        td {
            background-color: var(--dark-gray) !important;
            border: 0;
        }

        td td:hover {
            background-color: var(--dim-gray) !important;
        }

        table ~ table {
            margin: 0 !important;
            border: 0;
            text-align: center;
        }
    }

    #ftabfriends,
    #ftabfavd,
    #ftabfavs {
        margin-top: 2rem;
    }

    .clear {
        display: none;
    }

    #content > table + a {
        margin-left: 2rem;
    }

    /*********************************
    ***********  Album   *************
    *********************************/

    #page_body {
        background-image: none !important;
    }

    #content_wrapper_outer {
        background-color: var(--dark-gray) !important;
    }

    #content_wrapper {
        background-color: transparent !important;
    }

    .artist_left_col {
        background-color: var(--dark-gray);
    }

    .row,
    .page_release_art_frame,
    #column_container_right {
        background: var(--dark-gray);
    }

    #tracks {
        border-radius: 6px;
    }

    .rating_info_tab.active {
        background-color: var(--dim-blue) !important;
    }

    #chart_div,
    #chart_div2 {
        background: transparent !important;
    }
}

